import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

// Este componente maneja la entrada de b√∫squeda y la navegaci√≥n a los resultados
function SearchBar() {
    const [searchTerm, setSearchTerm] = useState('');
    const navigate = useNavigate();

    // Esta funci√≥n maneja el env√≠o del formulario de b√∫squeda
    const handleSubmit = (e) => {
        e.preventDefault();
        // Solo navegamos si hay un t√©rmino de b√∫squeda v√°lido
        if (searchTerm.trim()) {
            navigate(`/search/${encodeURIComponent(searchTerm.trim())}`);
            setSearchTerm(''); // Limpiamos el input despu√©s de buscar
        }
    };

    return (
        <form onSubmit={handleSubmit} className="d-flex">
            <input
                type="search"
                className="form-control me-2"
                placeholder="Buscar pel√≠culas..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                aria-label="Buscar pel√≠culas"
            />
            <button type="submit" className="btn btn-warning">
                üîç Buscar
            </button>
        </form>
    );
}

export default SearchBar;
