import React, { useState, useEffect } from 'react';
import movieService from '../services/api';
import MovieCard from '../components/MovieCard';

function HomePage() {
    // Estado para manejar las películas y la carga
    const [movies, setMovies] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);
    const [currentPage, setCurrentPage] = useState(1);
    const [totalPages, setTotalPages] = useState(0);

    // Efecto para cargar las películas cuando la página cambia
    useEffect(() => {
        // Función para cargar las películas populares
        const loadMovies = async () => {
            try {
                setIsLoading(true);
                const response = await movieService.getPopularMovies(currentPage);

                // Actualizamos el estado con los datos recibidos
                setMovies(response.data);
                setTotalPages(response.total_pages);
                setError(null);
            } catch (err) {
                setError('Error al cargar las películas. Por favor, intenta de nuevo más tarde.');
            } finally {
                setIsLoading(false);
            }
        };

        loadMovies();
    }, [currentPage]); // Se ejecuta cuando cambia la página

    // Función para manejar el cambio de página
    const handlePageChange = (newPage) => {
        setCurrentPage(newPage);
        window.scrollTo(0, 0); // Volvemos al principio de la página
    };

    // Renderizamos el componente de paginación
    const renderPagination = () => {
        return (
            <nav aria-label="Navegación de páginas">
                <ul className="pagination justify-content-center">
                    {/* Botón Anterior */}
                    <li className={`page-item ${currentPage === 1 ? 'disabled' : ''}`}>
                        <button
                            className="page-link"
                            onClick={() => handlePageChange(currentPage - 1)}
                            disabled={currentPage === 1}
                        >
                            Anterior
                        </button>
                    </li>

                    {/* Número de página actual */}
                    <li className="page-item active">
            <span className="page-link">
              Página {currentPage} de {totalPages}
            </span>
                    </li>

                    {/* Botón Siguiente */}
                    <li className={`page-item ${currentPage === totalPages ? 'disabled' : ''}`}>
                        <button
                            className="page-link"
                            onClick={() => handlePageChange(currentPage + 1)}
                            disabled={currentPage === totalPages}
                        >
                            Siguiente
                        </button>
                    </li>
                </ul>
            </nav>
        );
    };

    return (
        <div className="container py-4">
            {/* Cabecera de la página */}
            <header className="text-center mb-5">
                <h1 className="display-4">Películas Populares</h1>
                <p className="lead">Descubre las películas más populares del momento</p>
            </header>

            {/* Manejo de estados de carga y error */}
            {isLoading ? (
                <div className="text-center">
                    <div className="spinner-border text-light" role="status">
                        <span className="visually-hidden">Cargando...</span>
                    </div>
                </div>
            ) : error ? (
                <div className="alert alert-danger" role="alert">
                    {error}
                </div>
            ) : (
                <>
                    {/* Grid de películas */}
                    <div className="row row-cols-1 row-cols-sm-2 row-cols-md-3 row-cols-lg-4 g-4">
                        {movies.map(movie => (
                            <div className="col" key={movie.id}>
                                <MovieCard movie={movie} />
                            </div>
                        ))}
                    </div>

                    {/* Paginación */}
                    <div className="mt-5">
                        {renderPagination()}
                    </div>
                </>
            )}
        </div>
    );
}

export default HomePage;
