import React, { useState, useEffect } from 'react';
import { useSearchParams, Link } from 'react-router-dom';
import movieService from '../services/api';

function SearchPage() {
    const [searchParams] = useSearchParams();
    const [movies, setMovies] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const searchMovies = async () => {
            try {
                setIsLoading(true);
                const query = searchParams.get('query');
                const genres = searchParams.get('genres');

                console.log('Buscando con:', { query, genres }); // Para depuración

                const response = await movieService.searchMovies(query, genres ? genres.split(',') : []);
                console.log('Respuesta de búsqueda:', response); // Para depuración

                setMovies(response.data);
                setError(null);
            } catch (err) {
                console.error('Error en búsqueda:', err); // Para depuración
                setError('Error al buscar películas');
            } finally {
                setIsLoading(false);
            }
        };

        searchMovies();
    }, [searchParams]);

    if (isLoading) {
        return (
            <div className="container text-center py-5">
                <div className="spinner-border text-light" role="status">
                    <span className="visually-hidden">Cargando...</span>
                </div>
            </div>
        );
    }

    if (error) {
        return (
            <div className="container py-5">
                <div className="alert alert-danger">
                    {error}
                </div>
            </div>
        );
    }

    return (
        <div className="container py-5">
            <h2 className="text-white mb-4">
                Resultados de búsqueda
                {searchParams.get('query') && ` para: "${searchParams.get('query')}"`}
            </h2>

            {movies.length === 0 ? (
                <div className="alert alert-info">
                    No se encontraron películas.
                </div>
            ) : (
                <div className="row row-cols-1 row-cols-sm-2 row-cols-md-3 row-cols-lg-4 g-4">
                    {movies.map(movie => (
                        <div key={movie.id} className="col">
                            <Link to={`/movie/${movie.id}`} className="text-decoration-none">
                                <div className="card bg-dark text-white h-100">
                                    <img
                                        src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`}
                                        className="card-img-top"
                                        alt={movie.title}
                                        onError={(e) => {
                                            e.target.src = 'https://via.placeholder.com/500x750?text=No+Image';
                                        }}
                                    />
                                    <div className="card-body">
                                        <h5 className="card-title">{movie.title}</h5>
                                        <p className="card-text">
                                            {movie.release_date?.split('-')[0]}
                                        </p>
                                    </div>
                                </div>
                            </Link>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
}

export default SearchPage;
