import axios from 'axios';

// Alineamos nuestra configuración con tu backend
const BASE_URL = 'http://localhost:3000'; // Tu backend usa el puerto 3000 por defecto

const movieService = {
    // Obtiene películas populares - coincide con GET /movies/popular
    getPopularMovies: async (page = 1) => {
        try {
            const response = await axios.get(`${BASE_URL}/movies/popular`, {
                params: { page }
            });
            // Tu backend devuelve la respuesta con este formato específico
            return response.data;
        } catch (error) {
            console.error('Error al obtener películas populares:', error);
            throw error;
        }
    },

    // Búsqueda de películas - coincide con GET /movies/search/:query
    searchMovies: async (query, page = 1) => {
        try {
            const response = await axios.get(`${BASE_URL}/movies/search/${query}`, {
                params: { page }
            });
            return response.data;
        } catch (error) {
            console.error('Error en la búsqueda:', error);
            throw error;
        }
    },

    // Detalles de película con streaming - coincide con GET /movies/:id
    getMovieDetails: async (movieId) => {
        try {
            const response = await axios.get(`${BASE_URL}/movies/${movieId}`);
            return response.data;
        } catch (error) {
            console.error('Error al obtener detalles:', error);
            throw error;
        }
    },

    // Guardar película - coincide con POST /movies/save
    saveMovie: async (tmdb_id) => {
        try {
            const response = await axios.post(`${BASE_URL}/movies/save`, { tmdb_id });
            return response.data;
        } catch (error) {
            console.error('Error al guardar película:', error);
            throw error;
        }
    },

    // Obtener películas guardadas - coincide con GET /movies/saved
    getSavedMovies: async () => {
        try {
            const response = await axios.get(`${BASE_URL}/movies/saved`);
            return response.data;
        } catch (error) {
            console.error('Error al obtener guardadas:', error);
            throw error;
        }
    }
};

export default movieService;
