import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import movieService from '../services/api';
import './SearchBar.css';

function SearchBar() {
    const navigate = useNavigate();
    const [searchTerm, setSearchTerm] = useState('');
    const [genres, setGenres] = useState([]);
    const [selectedGenres, setSelectedGenres] = useState([]);
    const [showGenreFilter, setShowGenreFilter] = useState(false);

    useEffect(() => {
        const loadGenres = async () => {
            try {
                const response = await movieService.getGenres();
                console.log('Géneros cargados:', response); // Para depuración
                setGenres(response.data);
            } catch (error) {
                console.error('Error cargando géneros:', error);
            }
        };
        loadGenres();
    }, []);

    const handleGenreToggle = (genreId) => {
        setSelectedGenres(prevGenres => {
            if (prevGenres.includes(genreId)) {
                return prevGenres.filter(id => id !== genreId);
            } else {
                return [...prevGenres, genreId];
            }
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Construimos los parámetros de búsqueda
        const params = new URLSearchParams();

        if (searchTerm.trim()) {
            params.append('query', searchTerm.trim());
        }

        if (selectedGenres.length > 0) {
            params.append('genres', selectedGenres.join(','));
        }

        // Navegar a la página de búsqueda con los parámetros
        navigate(`/search?${params.toString()}`);

        // Mantener el término de búsqueda para referencia del usuario
        // setSearchTerm(''); // Comentamos esto para mantener el término visible
    };

    return (
        <form onSubmit={handleSubmit} className="search-form">
            <div className="search-container">
                <div className="input-group">
                    <input
                        type="search"
                        className="form-control"
                        placeholder="Buscar películas..."
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                    />
                    <button
                        type="button"
                        className="btn btn-outline-light"
                        onClick={() => setShowGenreFilter(!showGenreFilter)}
                    >
                        🏷️ Géneros
                    </button>
                    <button type="submit" className="btn btn-warning">
                        🔍 Buscar
                    </button>
                </div>

                {showGenreFilter && genres.length > 0 && (
                    <div className="genre-dropdown">
                        <div className="genre-list">
                            {genres.map(genre => (
                                <div key={genre.id} className="genre-item">
                                    <label className="genre-label">
                                        <input
                                            type="checkbox"
                                            checked={selectedGenres.includes(genre.id)}
                                            onChange={() => handleGenreToggle(genre.id)}
                                        />
                                        <span className="ms-2">{genre.name}</span>
                                    </label>
                                </div>
                            ))}
                        </div>
                    </div>
                )}

                {selectedGenres.length > 0 && (
                    <div className="selected-genres">
                        {selectedGenres.map(genreId => {
                            const genre = genres.find(g => g.id === genreId);
                            return genre && (
                                <span key={genreId} className="badge bg-secondary me-1">
                                    {genre.name}
                                    <button
                                        type="button"
                                        className="btn-close btn-close-white ms-2"
                                        onClick={() => handleGenreToggle(genreId)}
                                    />
                                </span>
                            );
                        })}
                    </div>
                )}
            </div>
        </form>
    );
}

export default SearchBar;
